import axios from 'axios';
export default {
  name: 'NotificationManager',
  data() {
    return {
      events: [],
      // Array per memorizzare gli eventi recuperati
      currentDate: new Date(),
      // Data corrente
      intervalId: null,
      // ID per l'intervallo che può essere utilizzato per fermarlo
      notifiedEvents: new Set()
    };
  },
  methods: {
    // Richiedi permesso per le notifiche di sistema
    async requestNotificationPermission() {
      try {
        if ('Notification' in window) {
          const permesso = await Notification.requestPermission();
          console.log("Permesso: ", permesso);
          if (permesso === 'granted') {
            console.log('Permesso notifiche di sistema concesso');
          } else {
            console.warn('Permesso notifiche di sistema negato');
          }
        }
      } catch (error) {
        console.error('Errore nella richiesta del permesso:', error);
      }
    },
    async sendEmailNotification(event, emailRicevente) {
      try {
        const token = sessionStorage.getItem('token');
        await axios.post('/api/events/email', {
          emailRicevente,
          eventDetails: event
        }, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        console.log('Notifica email inviata con successo');
      } catch (error) {
        console.error('Errore durante invio della notifica email:', error);
      }
    },
    sendSystemNotification(event) {
      if (!event) {
        console.warn("I dati dell'evento sono incompleti. Notifica non inviata.");
        return;
      }
      const notification = new Notification(`Nuovo Evento: ${event.title}`, {
        body: `L'evento inizia alle ${event.startTime} del giorno ${event.date}`
      });
      notification.onclick = () => {
        window.focus();
      };
    },
    sendAlertNotification(event) {
      // Verifica se l'evento è già stato notificato
      if (this.notifiedEvents.has(event.id)) {
        // Se la notifica è già stata inviata, non fare nulla
        return;
      }

      // Invia la notifica iniziale
      window.alert(`Nuovo Evento: ${event.title}\nInizio alle ${event.startTime} del giorno ${event.date}`);

      // Se repeatNotification è maggiore di 0, invia notifiche ripetute
      if (event.repeatNotification > 0) {
        for (let i = 0; i < event.repeatNotification; i++) {
          // Fai un delay per ogni ripetizione (ad esempio, puoi usare setTimeout)
          setTimeout(() => {
            window.alert(`Nuovo Evento: ${event.title}\nInizio alle ${event.startTime} del giorno ${event.date}`);
          }, (i + 1) * 1000); // Ogni ripetizione dopo 1 minuto (60000 ms)
        }
      }

      // Aggiungi l'evento all'elenco degli eventi notificati per evitare notifiche multiple
      this.notifiedEvents.add(event.id);
    },
    // Funzione per schedulare le notifiche
    async scheduleNotification(event, emailRicevente) {
      console.log("Schedulazione notifiche...");
      console.log("evento passato", event);
      try {
        console.log("Chiamata API iniziata...");
        const response = await axios.get('/api/getTime-machine');
        console.log("Time machine data:", response.data);
        const timeMachineNow = new Date(response.data).getTime(); // Ottieni il tempo attuale dalla Time Machine
        console.log("Time Machine Current Time:", timeMachineNow);

        // Crea la data dell'evento combinando event.date e event.startTime
        const eventDateTimeString = `${event.date.slice(0, 10)}T${event.startTime}:00`; // Combinazione di data e ora
        const eventTime = new Date(eventDateTimeString).getTime(); // Calcola il tempo dell'evento
        console.log("Event Time:", eventTime);

        // Verifica se eventTime è valido
        if (isNaN(eventTime)) {
          console.error("La data dell'evento non è valida:", eventDateTimeString);
          return;
        }
        const notificationTime = eventTime - event.notificationTime * 60000; // Calcola l'orario di notifica
        console.log("Notification Time:", notificationTime);
        const timeUntilNotification = notificationTime - timeMachineNow; // Calcola il tempo fino alla notifica
        console.log("Time Until Notification:", timeUntilNotification);
        if (isNaN(eventTime) || isNaN(notificationTime) || timeUntilNotification <= 0) {
          console.warn("L'orario della notifica è già passato o non è valido.");
          return;
        }
        if (timeUntilNotification > 0) {
          setTimeout(() => {
            console.log("trigger");
            this.triggerNotifications(event, emailRicevente);
            /*  if (event.repeatNotification > 0) {
               setInterval(() => {
                 this.triggerNotifications(event, emailRicevente);
               }, event.repeatNotification * 60000);
             }*/
          }, timeUntilNotification);
        } else {
          console.warn("L'ora di notifica è già passata o il tempo è impostato a meno di 0 minuti.");
        }
      } catch (error) {
        console.error("Errore durante la richiesta della Time Machine:", error);
      }
    },
    // Funzione per triggerare le notifiche
    triggerNotifications(event, emailRicevente) {
      console.log("evento notifica meccanismo:", event.notificationMechanism);

      // Verifica se la notifica per questo evento è già stata inviata
      if (this.notifiedEvents.has(event.id)) {
        return; // Se è già stata inviata, non fare nulla
      }
      if (event.notificationMechanism.includes('system')) {
        this.sendSystemNotification(event);
      }
      if (event.notificationMechanism.includes('email')) {
        this.sendEmailNotification(event, emailRicevente);
      }
      if (event.notificationMechanism.includes('alert')) {
        this.sendAlertNotification(event);
      }

      // Aggiungi l'evento all'elenco degli eventi notificati
      this.notifiedEvents.add(event.id);
    },
    // Funzione per ottenere gli eventi
    async fetchEvents() {
      try {
        const token = sessionStorage.getItem('token');
        const username = localStorage.getItem('username');
        const response = await axios.get('/api/eventsGET', {
          headers: {
            Authorization: `Bearer ${token}`
          },
          params: {
            author: username
          }
        });
        console.log(response.data);
        this.events = response.data.filter(event => new Date(event.date) >= this.currentDate);

        // Avvia lo scheduler per ciascun evento
        this.events.forEach(event => {
          const emailRicevente = localStorage.getItem('email');
          console.log("venti trovati");
          this.scheduleNotification(event, emailRicevente);
        });
      } catch (error) {
        console.error('Errore nel recupero degli eventi:', error);
      }
    },
    // Avvia l'aggiornamento periodico degli eventi
    startPeriodicUpdate() {
      this.intervalId = setInterval(() => {
        console.log("Aggiornamento eventi in corso...");
        this.fetchEvents();
      }, 1000); // Aggiorna ogni 10secondi (1000 ms)
    },
    // Ferma l'aggiornamento periodico
    stopPeriodicUpdate() {
      clearInterval(this.intervalId);
    }
  },
  mounted() {
    this.requestNotificationPermission(); //richiede il permesso per le notifiche di sistema
    this.fetchEvents(); // Recupera gli eventi inizialmente
    this.startPeriodicUpdate(); // Avvia l'aggiornamento periodico
  },
  beforeUnmount() {
    this.stopPeriodicUpdate(); // Ferma l'aggiornamento periodico quando il componente viene distrutto
  }
};
