      lastEventDate: '',
      lastActivityTitle: '',
      lastActivityDescription: '',
      lastActivityDeadline: '',
      noActivitiesMessage: '',
      currentDayActivities: [],
      allActivities: [],
      notifications: [],
      noNotificationsMessage: '',
      users: [],
      selectedUsers: [],
      notificationMessage: '',
      lastNoteHeading: '',
      lastNoteAuthor: '',
      noEventsMessage: '',
      allEvents: [],
