          alert(alertMessage);
          const startSession = confirm("Vuoi iniziare la sessione Pomodoro?");
          if (startSession) {
            sessionID.value = pomodoro.sessionID;
            // Logica per iniziare la sessione, per esempio, avvia il timer Pomodoro
            console.log("Inizia la sessione Pomodoro");
            // Qui puoi chiamare la funzione per avviare la sessione, come ad esempio:
            // startPomodoroSession(pomodoros);
          }
        }
      } catch (error) {
        console.error("Errore:", error);
      }
    };
    const newPom = ref({
      username: localStorage.getItem('username') || 'Guest',
      tempoStudio: 30,
      tempoPausa: 5,
      ripetizioni: 5,
      sessionID: uuidv4(),
      giorno: ''
    });
    const statusMessage = ref('');
    let studyCycles = ref(0);
    let timerInterval = null;
    let isStudyPhase = ref(true);
    let remainingTime = ref(0);
    let isPaused = ref(false);
    let isPomodoroSaved = false;
    const sessionIDP = ref(null);
    onMounted(() => {
      if (route.query.date) {
        newPom.value.giorno = route.query.date;
      }
      window.addEventListener('beforeunload', () => {
        if (!isPomodoroSaved) {
          savePomDataIntermedi();
        }
      });
      cercaPomodoro();
    });
    onBeforeUnmount(() => {
      window.removeEventListener('beforeunload', savePomDataIntermedi); //quando l'utente chiude la pag o chiude il browser
    });
    const savePomDataIntermedi = async () => {
      try {
        const token = sessionStorage.getItem('token');

        // Definisci le variabili per il tempo rimanente
        let savedTempoStudio;
        let savedTempoPausa;

        // Se siamo in fase di studio
        if (isStudyPhase.value) {
          savedTempoStudio = Math.floor(remainingTime.value / 60); // Calcola il tempo di studio completato
          savedTempoPausa = newPom.value.tempoPausa; // Usa il tempo di pausa predefinito
        } else {
          // Se siamo in pausa
          savedTempoStudio = newPom.value.tempoStudio; // Usa il tempo di studio predefinito
          savedTempoPausa = Math.floor(remainingTime.value / 60); // Calcola il tempo di pausa completato
        }

        // Crea il payload da inviare
        const pomData = {
          username: newPom.value.username,
          tempoStudio: savedTempoStudio,
          tempoPausa: savedTempoPausa,
          ripetizioni: studyCycles.value,
          sessionID: newPom.value.sessionID,
          giorno: newPom.value.giorno
        };
        console.log('Salvataggio pomData:', pomData); // Aggiungi questo log per il debug

        await axios.post('/api/savePomDataIntermedi', pomData, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
      } catch (error) {
        console.error('Errore nel salvataggio dei dati:', error);
      }
    };
    const aggiungiPomodoro = async () => {
      try {
        const token = sessionStorage.getItem('token');
        const currentDate = new Date();
        const selectedDate = new Date(newPom.value.giorno);
        const response = await axios.post('/api/pomsPOST', newPom.value, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        console.log('Sessione pomodoro aggiunta:', response.data);
        if (selectedDate.getTime() > currentDate.getTime()) {
          statusMessage.value = 'Sessione pomodoro aggiunta per una data futura.';
        } else {
          studyCycles.value = newPom.value.ripetizioni;
          startStudyTimer(newPom.value.tempoStudio, studyCycles.value, newPom.value.tempoPausa);
        }
      } catch (error) {
        console.error('Errore:', error);
      }
    };
    const startStudyTimer = async (studyTime, cycles, pause) => {
      if (cycles <= 0) {
        try {
          const token = sessionStorage.getItem('token');
          await axios.patch('/api/pomsUpdate', {
            sessionID: newPom.value.sessionID,
            terminato: true
          }, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          console.log('Ciclo marcato come terminato nel backend.');
        } catch (error) {
          console.error('Errore nell\'aggiornamento del ciclo:', error);
        }
        alert('Ciclo completato!');
        return;
      }
      statusMessage.value = `Studio! Numero cicli rimanenti: ${cycles}`;
      remainingTime.value = studyTime * 60;
      console.log(remainingTime.value);
      isStudyPhase.value = true;
      isPaused.value = false;
      clearInterval(timerInterval);
      timerInterval = setInterval(() => {
        updateTimer(remainingTime.value, studyTime, pause, cycles, startBreakTimer);
        remainingTime.value--;
      }, 1000);
    };
    const startBreakTimer = (pauseTime, cycles) => {
      if (cycles <= 0) {
        alert('Ciclo completato!');
        return;
      }
      statusMessage.value = `Pausa! Numero cicli rimanenti: ${cycles}`;
      remainingTime.value = pauseTime * 60;
      isStudyPhase.value = false;
      isPaused.value = false;
      clearInterval(timerInterval);
      timerInterval = setInterval(() => {
        updateTimer(remainingTime.value, pauseTime, pauseTime, cycles, startStudyTimer);
        remainingTime.value--;
      }, 1000);
    };
    const updateTimer = (remaining, phaseTime, pauseTime, cycles, nextPhase) => {
      if (remaining > 0) {
        const minutes = Math.floor(remaining / 60);
        const seconds = remaining % 60;
        document.getElementById('timerDisplay').textContent = `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
      } else {
        clearInterval(timerInterval);
        studyCycles.value--;
        nextPhase(phaseTime, studyCycles.value);
      }
    };
    const skipPhase = async () => {
      clearInterval(timerInterval);
      if (isStudyPhase.value) {
        studyCycles.value--;
        startBreakTimer(newPom.value.tempoPausa, studyCycles.value);
      } else {
        startStudyTimer(newPom.value.tempoStudio, studyCycles.value, newPom.value.tempoPausa);
      }
      if (studyCycles.value <= 0) {
        try {
          const token = sessionStorage.getItem('token');
          await axios.patch('/api/pomsUpdateFinito', {
            sessionID: newPom.value.sessionID,
            terminato: true
          }, {
            headers: {
              Authorization: `Bearer ${token}`
            }
          });
          console.log('Ciclo marcato come terminato nel backend.');
        } catch (error) {
          console.error('Errore nell\'aggiornamento del ciclo:', error);
        }
        statusMessage.value = "Ciclo completato!";
        document.getElementById('timerDisplay').textContent = "00:00";
      }
    };
    const restartCycle = () => {
      clearInterval(timerInterval);
      studyCycles.value = newPom.value.ripetizioni;
      startStudyTimer(newPom.value.tempoStudio, studyCycles.value, newPom.value.tempoPausa);
    };
    const endCycle = async () => {
      clearInterval(timerInterval);
      studyCycles.value = 0;
      try {
        const token = sessionStorage.getItem('token');
        await axios.patch('/api/pomsUpdate', {
          sessionID: newPom.value.sessionID,
          terminato: true
        }, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        console.log('Ciclo marcato come terminato nel backend.');
      } catch (error) {
        console.error('Errore nell\'aggiornamento del ciclo:', error);
      }
      statusMessage.value = 'Ciclo terminato manualmente.';
      document.getElementById('timerDisplay').textContent = "00:00";
    };
    const pauseTimer = () => {
      clearInterval(timerInterval);
      savePomDataIntermedi();
      isPomodoroSaved = true;
      isPaused.value = true;
    };
    const resumeTimer = () => {
      isPaused.value = false;
      clearInterval(timerInterval);
      timerInterval = setInterval(() => {
        updateTimer(remainingTime.value, newPom.value.tempoStudio, newPom.value.tempoPausa, studyCycles.value, isStudyPhase.value ? startBreakTimer : startStudyTimer);
        remainingTime.value--;
      }, 1000);
    };
    return {
      newPom,
      statusMessage,
      aggiungiPomodoro,
      skipPhase,
      restartCycle,
      endCycle,
      pauseTimer,
      resumeTimer,
      isPaused
    };
  }
};
